)}                                }                                .pickerStyle(MenuPickerStyle())                                .frame(maxWidth: .infinity, alignment: .leading)                                .padding(10)                                .background(Color.gray.opacity(0.1))                                .cornerRadius(8)                                .colorMultiply(.black) }                            HStack {                                Text("Condition:")                                    .frame(width: 110, alignment: .leading)                                    .foregroundColor(.gray)                                Picker("Condition", selection: $condition)                                {                                    ForEach(conditions, id: \.self) { Text($0) }                                }                                .pickerStyle(MenuPickerStyle())                                .frame(maxWidth: .infinity, alignment: .leading)                                .padding(10)                                .background(Color.gray.opacity(0.1))                                .cornerRadius(8).colorMultiply(.black)                                                           }                            HStack {                                Text("Image:")                                    .frame(width: 110, alignment: .leading)                                    .foregroundColor(.gray)                                PhotosPicker(selection: $imageSelection, matching: .images) {                                    HStack {                                        Image(systemName: "photo")                                    .colorMultiply(.black)                                    Text("Select Item Image")                                    .foregroundColor(.primaryColor)                                    }                                    .padding()                                    .frame(maxWidth: .infinity)                                    .background(Color.gray.opacity(0.2))                                    .cornerRadius(10)                                }                            }                            if let image = selectedImage {                                VStack(alignment: .leading, spacing: 8) {                                    Image(uiImage: image)                                        .resizable()                                        .scaledToFit()                                        .frame(height: 150)                                        .cornerRadius(10)                                    Button(action: {                                        selectedImage = nil                                        imageSelection = nil                                    }) {                                        HStack {                                            Image(systemName: "trash")                                            Text("Remove Image")                                        }                                        .foregroundColor(.red)                                    }                                }                            }                            VStack(alignment: .leading, spacing: 10) {                                Text("Seller Details")                                    .font(.headline)                                    .foregroundColor(.primary)                                    .padding(.bottom, 4)                                LabeledTextField(label: "Seller Name", text: $sellerName)                                LabeledTextField(label: "Email", text: $email)                                LabeledTextField(label: "Contact", text: $contact)                                LabeledTextField(label: "Location", text: $location)                            }                            HStack(spacing: 16) {                                Button(action: createBid) {                                    Text("Create Bid")                                        .fontWeight(.semibold)                                        .frame(maxWidth: .infinity)                                        .padding()                                        .background(Color.primaryColor)                                        .foregroundColor(.white)                                        .cornerRadius(10)                                }                                Button(action: resetForm) {                                    Text("Reset Form")                                        .fontWeight(.semibold)                                        .frame(maxWidth: .infinity)                                        .padding()                                        .background(Color.red.opacity(0.8))                                        .foregroundColor(.white)                                        .cornerRadius(10)                                }                            }                            Color.clear.frame(height: 0).id("bottom")                        }                        .padding()                    }                }                Divider()                Footer_View()            }            .navigationBarTitleDisplayMode(.inline)            .toolbar {                ToolbarItem(placement: .principal) {                    Text("Create Bid")                        .font(.title3)                        .fontWeight(.bold)                        .foregroundColor(.primaryColor)                }            }        }        .onChange(of: imageSelection) { newItem in            Task {                if let data = try? await newItem?.loadTransferable(type: Data.self),                   let uiImage = UIImage(data: data) {                    selectedImage = uiImage                }            }        }    }    func createBid() {        let newItem = Bid_Item(            id: itemID,            itemName: itemName,            description: description,            category: category,            condition: condition,            image: selectedImage,            sellerName: sellerName,            email: email,            contact: contact,            location: location,            createdDate: Date()        )        bidManager.addItem(newItem)        resetForm()    }    func resetForm() {        itemID = UUID()        itemName = ""        description = ""        category = "Electronics"        condition = "Brand New"        selectedImage = nil        imageSelection = nil        sellerName = ""        email = ""        contact = ""        location = ""    }}struct LabeledTextField: View {    let label: String    @Binding var text: String    var body: some View {        HStack {            Text("\(label):")                .frame(width: 110, alignment: .leading)                .foregroundColor(.gray)            TextField(label, text: $text)                .padding(10)                .background(Color.black.opacity(0.1))                .cornerRadius(8)        }    }}class BidManager: ObservableObject {    @Published var bidItems: [Bid_Item] = []    func addItem(_ item: Bid_Item) {        bidItems.append(item)    }}struct Bid_Item: Identifiable {    let id: UUID    let itemName: String    let description: String    let category: String    let condition: String    let image: UIImage?    let sellerName: String    let email: String    let contact: String    let location: String    let createdDate: Date}// Footer Viewstruct Footer_View: View {    var body: some View {        HStack {            Button(action: {}) {                VStack {                    Image(systemName: "house.fill")                    Text("Home").font(.footnote)                }            }            Spacer()            Button(action: {}) {                VStack {                    Image(systemName: "chart.bar.fill")                    Text("Dashboard").font(.footnote)                }            }            Spacer()            Button(action: {}) {                VStack {                    Image(systemName: "heart.fill")                    Text("Wishlist").font(.footnote)                }            }            Spacer()            Button(action: {}) {                VStack {                    Image(systemName: "checkmark.circle.fill")                    Text("Completed").font(.footnote)                }            }            Spacer()            Button(action: {}) {                VStack {                    Image(systemName: "gearshape.fill")                    Text("Settings").font(.footnote)                }            }        }        .padding()        .foregroundColor(.primaryColor)        .background(Color(UIColor.systemBackground))    }}struct CreateBidView_Previews: PreviewProvider {    static var previews: some View {        CreateBidView()            .environmentObject(BidManager())    }}